package com.excercise.items.dao;import com.excercise.items.model.Item;import org.springframework.context.annotation.Configuration;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.transaction.annotation.EnableTransactionManagement;import org.springframework.transaction.annotation.Transactional;@Configuration@EnableTransactionManagementpublic interface ItemDao extends JpaRepository<Item, Long> {  @Modifying  @Transactional  @Query("delete from Item i where i.modifiedTs < :timestamp")  void deleteBeforeTimestamp(long timestamp);}