package com.excercise.items.model;import jakarta.persistence.*;import java.util.ArrayList;import java.util.List;@Entity@Table(name = "items", indexes = {@Index(columnList = "modifiedTs")})public class Item {  @Id  @GeneratedValue  private Long id;  @Column(name = "value")  private int value;  @ElementCollection  private List<String> tags = new ArrayList<>();  @Column(name = "createdTs")  private long createdTs;  @Column(name = "modifiedTs")  private long modifiedTs;  @Column(name = "revision")  private int revision;  public Item() {  }  public Item(int value, List<String> tags) {    this.value = value;    this.tags = tags != null ? tags : new ArrayList<>();  }  public Long getId() {    return id;  }  public int getValue() {    return value;  }  public void setValue(int value) {    this.value = value;  }  public List<String> getTags() {    return tags;  }  public void setTags(List<String> tags) {    this.tags = tags != null ? tags : new ArrayList<>();  }  public int getRevision() {    return revision;  }  public void setRevision(int revision) {    this.revision = revision;  }  public long getCreatedTs() {    return createdTs;  }  public void setCreatedTs(long createdTs) {    this.createdTs = createdTs;  }  public long getModifiedTs() {    return modifiedTs;  }  public void setModifiedTs(long modifiedTs) {    this.modifiedTs = modifiedTs;  }}